package com.unistack.tamboo.mgt.config;import org.springframework.boot.context.embedded.ConfigurableEmbeddedServletContainer;import org.springframework.boot.context.embedded.EmbeddedServletContainerCustomizer;import org.springframework.boot.context.embedded.MimeMappings;import org.springframework.context.annotation.Configuration;/** * 配置MIME支持直接点击下载zip,rar包 */@Configurationpublic class MimeTypeConfig implements EmbeddedServletContainerCustomizer {	@Override	public void customize(ConfigurableEmbeddedServletContainer container) {		MimeMappings mimeMapping = new MimeMappings(MimeMappings.DEFAULT);		mimeMapping.add("rar", "application/octet-stream");		mimeMapping.add("zip", "application/octet-stream");		container.setMimeMappings(mimeMapping);			}}